import React, { FC, useState, useEffect } from "react";


export interface IProps {
   onClick:any;
   answer: any;
   onTypeTextReponseValueHandler: any;
}


export const AnswerOption: FC<IProps> = (props: IProps) => {    

    const [getTextResponses, setTextResponses] = useState<any>({
        elementId:0,
        textTyped:""
    });
    return (
      
            <div
                className="bg-gray-100 dark:bg-gray-900 mx-6 h-16 w-auto mb-4 rounded-md flex items-center"
                onClick={() => props.onClick(props.answer)}
            > 
            { props.answer.type=="option"?(
                <div>
                 <input
                    type="radio"
                    name="answer"
                    value={props.answer}
                    className="ml-5 dark:bg-gray-800"
                />
                <label className="text-gray-700 dark:text-gray-400 text-lg ml-4">
                        {props.answer.Name}
                    </label>
                </div>
            ):
            <div>
             <label className="text-gray-700 dark:text-gray-400 text-lg ml-4">
                    {props.answer.Name}
            </label>
                <input
                type="text"
                name="answer"
                value={ props.onTypeTextReponseValueHandler(this, props.answer.Id)}
                className="ml-5 dark:bg-gray-800 text-gray-700
                bg-white bg-clip-padding
                border border-solid border-gray-300
                rounded"
                />
            </div>
            }
               
                
            </div>
        
    );
};
